import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import 'react-toastify/dist/ReactToastify.css';

function SingleBooking() {
    const bookingReceive = { date: '', name: '', content: '' };
    const [booking, setBooking] = useState(bookingReceive);
    const [update_booking, setUpdateBooking] = useState(bookingReceive);
    const [msgReceive, setMsgReceive] = useState();

    // Step 1: To collect the ID from the URL, i.e. Param
    const { id } = useParams();
    // console.log(id);

    // Step 2: To perform Search based on the ID
    useEffect(() => {
        axios.get(`http://localhost:7700/myBooking/booking/${id}`)
            .then((response) => {
                // console.log(response.data);
                setBooking(response.data);
            });
    }, [id]);

    // console.log(booking);

    const handleSubmit = async (event) => {
        event.preventDefault();


        if (!update_booking.title || !update_booking.content) {
            toast.error("Title and Content are Required!");
            return;
        }

        try {
            const response = await axios.put(`http://localhost:7700/myBooking/booking_edit/${id}`, update_booking);
            setMsgReceive(response.data);
            toast.success('Appointment updated successfully!');
            console.log(response);
        } catch (error) {
            console.log(error);
            toast.error('Failed to update Appointment.')
        }
    }

    const handleChange = (event) => {

        const { name, value } = event.target;

        setUpdateBooking({ ...update_booking, [name]: value });


    }

    return (
        <div className="AppStyle" >
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', background: "#fofofo" }}>

                <div style={{ display: 'flex', marginBottom: '1rem' }}>
                    <label htmlFor="date" className="col-sm-2 col-form-label">date</label>
                    <input type="date" className="form-control" placeholder={update_booking.date} min={booking.date} name="date" onChange={handleChange} style={{ marginLeft: '0.5rem' }} />
                </div>

                <div style={{ display: 'flex', marginBottom: '1rem' }}>
                    <label htmlFor="name" className="col-sm-2 col-form-label">Name</label>
                    <input type="text" className="form-control" placeholder={update_booking.name} name="name" onChange={handleChange} style={{ marginLeft: '0.5rem' }} />
                </div>

                <div style={{ display: 'flex', marginBottom: '1rem' }}>
                    <label htmlFor="content" className="col-sm-2 col-form-label">Content</label>
                    <input type="text" className="form-control" placeholder={update_booking.content} name="content" onChange={handleChange} style={{ marginLeft: '0.5rem' }} />
                </div>

                <button type="submit" className="btn btn-primary" style={{ width: 'fit-content', alignSelf: 'center' }}>Update</button>

            </form>

            {msgReceive && (
                <div style={{ color: "white", marginTop: "10px" }}>
                    {msgReceive.message}
                </div>
            )}

        </div>
    )
}

export default SingleBooking
