import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import 'react-toastify/dist/ReactToastify.css';

function SingleBooking() {
    const bookingReceive = { date: '', name: '', content: '' };
    const [booking, setBooking] = useState(bookingReceive);
    const [update_booking, setUpdateBooking] = useState(bookingReceive);
    const [msgReceive, setMsgReceive] = useState();

    // Step 1: To collect the ID from the URL, i.e. Param
    const { id } = useParams();
    // console.log(id);

    // Step 2: To perform Search based on the ID
    useEffect(() => {
        axios.get(`http://localhost:7700/myBooking/booking/${id}`)
            .then((response) => {
                // console.log(response.data);
                setBooking(response.data);
            });
    }, [id]);

    // console.log(booking);

    const handleSubmit = async (event) => {
        event.preventDefault();


        if (!update_booking.name || !update_booking.content) {
            toast.error("Title and Content are Required!");
            return;
        }

        try {
            const response = await axios.put(`http://localhost:7700/myBooking/booking_edit/${id}`, update_booking);
            setMsgReceive(response.data);
            toast.success('Appointment updated successfully!');
            console.log(response);
        } catch (error) {
            console.log(error);
            toast.error('Failed to update Appointment.')
        }
    }

    const handleChange = (event) => {

        const { name, value } = event.target;

        setUpdateBooking({ ...update_booking, [name]: value });


    }

    return (
        <div className="AppStyle" >
            <form onSubmit={handleSubmit} className="AppStyle">
    <div className="row">
        <div className="col-sm-2">
            <label htmlFor="date" className="col-form-label">Date</label>
        </div>
        <div className="col-sm-10">
            <input type="date" className="form-control" placeholder={booking.date} min={booking.date} name="date" onChange={handleChange} />
        </div>
    </div>

    <div className="row">
        <div className="col-sm-2">
            <label htmlFor="name" className="col-form-label">Name</label>
        </div>
        <div className="col-sm-10">
            <input type="text" className="form-control" placeholder={booking.name} name="name" onChange={handleChange} />
        </div>
    </div>

    <div className="row">
        <div className="col-sm-2">
            <label htmlFor="content" className="col-form-label">Content</label>
        </div>
        <div className="col-sm-10">
            <input type="text" className="form-control" placeholder={booking.content} name="content" onChange={handleChange} />
        </div>
    </div>

    <button type="submit" className="btn btn-primary" style={{ width: 'fit-content', alignSelf: 'center', marginTop: '1rem' }}>Update</button>
</form>


            {msgReceive && (
                <div style={{ color: "white", marginTop: "10px", textAlign:"center"}}>
                    {msgReceive.message}
                </div>
            )}

        </div>
    )
}

export default SingleBooking
